CREATE OR REPLACE VIEW `vw_access_rights_terminate_loginid` AS SELECT ULD.ULD_LOGINID,URCN.URC_ID,URCN.URC_DATA,UA.UA_JOIN_DATE FROM USER_LOGIN_DETAILS ULD,USER_ACCESS UA,VW_SUB_ACCESS_RIGHTS_MAXRECVER V,USER_RIGHTS_CONFIGURATION URCN,ROLE_CREATION RC WHERE ULD.ULD_ID=UA.ULD_ID AND UA.UA_REC_VER=V.RECVER AND V.ULD_ID=UA.ULD_ID AND V.ULD_ID=ULD.ULD_ID AND UA.RC_ID=RC.RC_ID AND RC.URC_ID=URCN.URC_ID AND UA.UA_TERMINATE IS NULL GROUP BY ULD.ULD_ID ;

